    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
      double totalBelanja = Double.parseDouble(txt_total.getText());
                String kodePelanggan = txt_kode.getText();

                Diskon diskonBelanja = new Diskon(totalBelanja, kodePelanggan);
                diskonBelanja.hitungDiskon();

                jTextArea1.setText("Total Belanja: " + diskonBelanja.getTotalBelanja() + "\n"
                        + "Diskon yang Didapatkan: " + diskonBelanja.getDiskon() + "\n"
                        + "Total Bayar: " + diskonBelanja.getTotalBayar());
    }//GEN-LAST:event_jButton1ActionPerformed

